  get_language(){
    var lang = navigator.language || navigator.userLanguage || 'en';
    var language = lang.toString().toLowerCase()
    if(language.includes('-')){
      var ln = language.split('-')
      language = ln[0]
    }
    // console.log(language)
    return language
  }

