  get_time_of_day_theme(){
    var hour = new Date().getHours() 
    if(hour >= 18 || hour < 7){
      if(hour >= 23 || hour < 4){
        return this.getLocale()['2740']/* midnight */
      }
      return this.getLocale()['1418']/* 'dark' */
    }else{
      return this.getLocale()['1417']/* 'light' */
    }
  }

