  decrypt_storage_data_using_key(data, key){
    try{
      var bytes  = CryptoJS.AES.decrypt(data, key);
      var originalText = bytes.toString(CryptoJS.enc.Utf8);
      return originalText
    }catch(e){
      return data
    }
  }

