  get_ecid_file_password_if_any = async (ecid) => {
    if(!this.state.uploaded_data_cids.includes(ecid)) return 'e';
    var ecid_obj = this.get_cid_split2(ecid)
    if(this.state.uploaded_data[ecid_obj['filetype']] == null) return 'e';
    var _data = this.state.uploaded_data[ecid_obj['filetype']][ecid_obj['full']];
    if(_data == null) return 'e';
    if(_data['encrypted'] != true) return 'e';
    const file_name = _data['name'];
    const private_key = this.state.accounts['E25'].privateKey.toString()
    const password = this.hash_data_with_randomizer(file_name + _data['id'] + private_key)
    return await this.encrypt_data_string(password, process.env.REACT_APP_FILE_NAME_ENCRYPTION_KEY);
  }

  update_object_change_in_db = (state, type) => {
    const type_as_string = type.toString()
    const final_data = structuredClone(state)
    final_data.last_modified = Date.now()
    var clone = structuredClone(this.state.new_object_changes)
    if(clone[type_as_string] == null){
      clone[type_as_string] = {}
    }
    clone[type_as_string][state.id] = final_data
    this.setState({new_object_changes: clone})
    this.set_cookies_after_stack_action()
  }

  fetch_objects_from_db = (type) => {
    const type_as_string = type.toString();
    return this.state.new_object_changes[type_as_string] || {};
  }

  delete_object_in_change_db(id, type){
    const type_as_string = type.toString()
    var clone = structuredClone(this.state.new_object_changes)
    if(clone[type_as_string] != null && clone[type_as_string][id] != null){
      delete clone[type_as_string][id]
      this.setState({new_object_changes: clone})
    }
  }

  can_sender_include_image_in_markdown(){
    const users_tx_data = this.senders_transaction_count()
    if(users_tx_data.transaction_count < this.state.media_activation_tx_limit){
      return false
    }
    else if(this.state.media_activation_age_limit > (Date.now()/1000) - users_tx_data.oldest_transaction_time){
      return false
    }
    return true
  }

  











