  load_queue = async (queue, pos) => {
    const songs_to_load = []
    const lyrics_to_load = []

    const songs_to_load2 = []
    const lyrics_to_load2 = []
    const keys = {}
    for(var i=pos; i<queue.length; i++){
      const song = queue[i]
      if(i == 0){
        songs_to_load.push(song['track'])
        if(song['subtitle_type'] == 'upload'){
          lyrics_to_load.push(song['lyrics'])
        }else{
          lyrics_to_load.push('')
        }
      }else{
        songs_to_load2.push(song['track'])
        if(song['subtitle_type'] == 'upload'){
          lyrics_to_load2.push(song['lyrics'])
        }else{
          lyrics_to_load2.push('')
        }
      }
      const key_object = song['object']['ipfs'].ecid_encryption_passwords || {}
      var object_keys = Object.keys(key_object)
      object_keys.forEach(ecid => {
        keys[ecid] = key_object[ecid]
      });
    }

    for(var j=0; j<songs_to_load.length; j++){
      const song = songs_to_load[j]
      const lyrics = lyrics_to_load[j]

      var has_played = false;
      if(j == 0 && this.has_file_loaded(song) == true){
        //if its the song thats to be played
        await this.wait(750)
        this.audio_pip_page.current?.start_playing()
        this.setState({play_pause_state: 1/* playing */})
        has_played = true
      }

      if(lyrics != ''){
        await this.fetch_uploaded_data_from_ipfs([song, lyrics], false, keys)
      }else{
        await this.fetch_uploaded_data_from_ipfs([song], false, keys)
      }
      if(j == 0 && !has_played){
        //if its the song thats to be played
        await this.wait(750)
        this.audio_pip_page.current?.start_playing()
        this.setState({play_pause_state: 1/* playing */})
      }
    }

    const load_data = []
    for(var k=0; k<songs_to_load2.length; k++){
      const song = songs_to_load2[k]
      const lyrics = lyrics_to_load2[k]
      
      load_data.push(song)
      if(lyrics != ''){
        load_data.push(lyrics)
      }
    }

    await this.fetch_uploaded_data_from_ipfs(load_data, false, keys)
  }

  has_file_loaded(audio_file){
    var ecid_obj = this.get_cid_split(audio_file)
    if(this.state.uploaded_data[ecid_obj['filetype']] == null) return false
    var data = this.state.uploaded_data[ecid_obj['filetype']][ecid_obj['full']]
    if(data == null) return false
    if(data['data'] == null) return false
    return true
  }

