  get_subscription_data = async (contractInstance, F5contractInstance, account, web3, e5, contract_addresses, E52contractInstance, prioritized_accounts, specific_items, return_created_object_events_only=false, all_return_data={}) => {
    var created_subscription_events = null
    var my_paid_subscription_events = null
    var created_index_events = null
    var payment_history_events = null

    if(this.state.beacon_node_enabled == true){
      var event_params = [
        [web3, contractInstance, 'e1', e5, {p2/* object_type */:33/* subscription_object */ }],
        [web3, F5contractInstance, 'e1', e5, {p2/* sender_acc_id */:account }],
        [web3, E52contractInstance, 'e2', e5, {p3/* item_type */: 33/* subscription_object */, p1:this.get_valid_post_index(web3)}],
        [web3, F5contractInstance, 'e1', e5, {p2/* sender_acc_id */:account}]
      ]
      var all_events = this.state.saved_pre_launch_events[e5] != null && (this.state.created_subscriptions[e5] == null || this.state.created_subscriptions[e5].length < 5 || return_created_object_events_only == true) ? [
        this.state.saved_pre_launch_events[e5]['created_subscription_events'],
        this.state.saved_pre_launch_events[e5]['my_paid_subscription_events'],
        this.state.saved_pre_launch_events[e5]['created_subscripion_index_events'],
        this.state.saved_pre_launch_events[e5]['payment_history_events'],
      ] : (await this.load_multiple_events_from_nitro(event_params)).all_events

      created_subscription_events = all_events[0]
      my_paid_subscription_events = all_events[1]
      created_index_events = all_events[2]
      payment_history_events = all_events[3]
    }
    else{
      created_subscription_events = await this.load_event_data(web3, contractInstance, 'e1', e5, {p2/* object_type */:33/* subscription_object */ })

      my_paid_subscription_events = await this.load_event_data(web3, F5contractInstance, 'e1', e5, {p2/* sender_acc_id */:account })

      created_index_events = await this.load_event_data(web3, E52contractInstance, 'e2', e5, {p3/* item_type */: 33/* subscription_object */, p1:this.get_valid_post_index(web3)})

      payment_history_events = await this.load_event_data(web3, F5contractInstance, 'e1', e5, {p2/* sender_acc_id */:account})
    }

    var valid_ids = this.get_ids_from_events(created_index_events)
    var created_subscription_events = created_subscription_events.filter(function (event) {
      return (valid_ids.includes(event.returnValues.p1))
    })

    var my_paid_subs = []
    var my_paid_subscription_e5_ids = []
    my_paid_subscription_events.forEach(event => {
      if(!my_paid_subs.includes(event.returnValues.p1)){
        my_paid_subs.push(event.returnValues.p1)
        my_paid_subscription_e5_ids.push(event.returnValues.p1+e5)
      }
    });

    var my_paid_subscription_e5_ids_clone = this.state.my_paid_subscription_e5_ids.slice()
    my_paid_subscription_e5_ids_clone = my_paid_subscription_e5_ids_clone.concat(my_paid_subscription_e5_ids)
    this.setState({my_paid_subscription_e5_ids: my_paid_subscription_e5_ids_clone})

    if(prioritized_accounts && prioritized_accounts.length > 0){
      var my_posted_events = created_subscription_events.filter(function (event) {
        return (prioritized_accounts.includes(parseInt(event.returnValues.p3/* sender_account_id */)) || prioritized_accounts.includes(parseInt(event.returnValues.p1/* object_id */)))
      })
      created_subscription_events = my_posted_events
    }else{
      //prioritize my content first
      var my_posted_events = created_subscription_events.filter(function (event) {
        return (event.returnValues.p3/* sender_account_id */ == account)
      })
      created_subscription_events.forEach(event => {
        if(my_paid_subs.includes(event.returnValues.p1)){
          if(my_posted_events.find(e => e.returnValues.p1 === event.returnValues.p1) == null){
            my_posted_events.push(event)
          }
        }
      });
      created_subscription_events.forEach(event => {
        if(my_posted_events.find(e => e.returnValues.p1 === event.returnValues.p1) == null){
          my_posted_events.push(event)
        }
      });
      created_subscription_events = my_posted_events
    }

    if(specific_items != null && specific_items.length > 0){
      var my_events = created_subscription_events.filter(function (event) {
        return (specific_items.includes(event.returnValues.p1))
      })
      created_subscription_events.forEach(event => {
        if(my_events.find(e => e.returnValues.p1 === event.returnValues.p1) == null){
          my_events.push(event)
        }
      });
      created_subscription_events = my_events
    }

    if(return_created_object_events_only == true){
      // console.log('get_subscription_data', 'returning events only: ', created_subscription_events, this.state.saved_pre_launch_events[e5]['created_subscription_events'], created_index_events)
      return created_subscription_events
    }

    var created_subscriptions = []
    for(var i=0; i<created_subscription_events.length; i++){
      var id = created_subscription_events[i].returnValues.p1
      created_subscriptions.push(id)
    }

    this.record_number_of_items(e5, 'subscriptions', created_subscriptions.length)
    var sort_objects_into_3d_array = async (created_subscriptions, all_return_data) => {
      const object_array = []
      const unloaded_items = {}
      created_subscriptions.forEach(id => {
        if(all_return_data[e5]['objects_data']['object_data'][id] != null){
          object_array.push(all_return_data[e5]['objects_data']['object_data'][id])
        }else{
          var data_sizes = [ 20, 20, 0, 0, 0 ]
          var my_default_object = []
          data_sizes.forEach(element => {
            var arr = []
            for(var q=0; q<element; q++){
              arr.push(0)
            }
            my_default_object.push(arr)
          });
          object_array.push(my_default_object)
          unloaded_items[id] = object_array.length-1
        }
      });
      if(Object.keys(unloaded_items).length > 0){
        const unloaded_objects = await F5contractInstance.methods.f74(Object.keys(unloaded_items)).call((error, result) => {});
        Object.keys(unloaded_items).forEach((object_id, index)=> {
          object_array[unloaded_items[object_id]] = unloaded_objects[index]
        });
      }
      return object_array
    }
    var created_subscription_data = all_return_data[e5] != null ? await sort_objects_into_3d_array(created_subscriptions, all_return_data) : await F5contractInstance.methods.f74(created_subscriptions).call((error, result) => {});
    var created_subscription_object_data = this.state.created_subscriptions[e5] == null ? [] : this.state.created_subscriptions[e5].slice()
    var created_subscription_object_mapping = this.state.created_subscription_object_mapping[e5] == null ? {} : structuredClone(this.state.created_subscription_object_mapping[e5])
    var is_first_time = this.state.created_subscriptions[e5] == null
    

    var account_as_list = []
    for(var i=0; i<created_subscriptions.length; i++){
      account_as_list.push([account])
    }
    // console.log('get_subscription_data', 'calling my_payments_for_all_subscriptions f229')
    var my_payments_for_all_subscriptions = created_subscriptions.length == 0 ? [] : await F5contractInstance.methods.f229(created_subscriptions, account_as_list).call((error, result) => {});

    var starter = my_paid_subs.length > 53 ? my_paid_subs.length : 53;
    const all_return_data_loaded_event_count = created_subscriptions.slice(0, starter).length
    // console.log('get_subscription_data', 'calling fetch_multiple_objects_data...')
    var all_data = await this.fetch_multiple_objects_data(created_subscriptions.slice(0, this.state.sliced_object_load_count), web3, e5, contract_addresses, all_return_data, 'objects_data')
    // console.log('get_subscription_data', 'done calling fetch_multiple_objects_data...', all_data)

    var load_pos = this.state.sliced_object_load_count
    for(var i=0; i<created_subscriptions.length; i++){
      var subscription_data = all_data[created_subscriptions[i]] == null ? await this.fetch_objects_data(created_subscriptions[i], web3, e5, contract_addresses) : all_data[created_subscriptions[i]]
      var my_payment = my_payments_for_all_subscriptions[i]

      
      var subscription_config = created_subscription_data[i][1]
      var time_unit = subscription_config[5] == 0 ? 60*53 : subscription_config[5]
      var last_expiration_time = this.get_last_expiration_time(payment_history_events, created_subscriptions[i], time_unit, my_payment/* [0] */[0] )

      var subscription_object = {'id':created_subscriptions[i], 'e5_id':created_subscriptions[i]+e5, 'data':created_subscription_data[i], 'ipfs':subscription_data, 'event':created_subscription_events[i], 'payment':my_payment[0], 'paid_accounts':[]/* paid_accounts */, 'paid_amounts':[]/* paid_amounts */, 'moderators':[]/* moderators */, 'access_rights_enabled':false/* interactible_checker_status_values[0] */, 'e5':e5, 'timestamp':created_subscription_events[i].returnValues.p4, 'author':created_subscription_events[i].returnValues.p3, 'last_expiration_time':last_expiration_time, 'hidden':true, 'pos':created_subscription_object_data.length, 'object_type':'subscription'}

      if(this.homepage.current?.state.selected_subscription_item == created_subscriptions[i]+e5){
        const previous_obj = this.state.created_subscriptions[e5].find(e => e['e5_id'] == created_subscriptions[i]+e5)
        if(previous_obj != null){
          subscription_object['moderators'] = previous_obj['moderators'] 
          subscription_object['paid_accounts'] = previous_obj['paid_accounts']
          subscription_object['paid_amounts'] = previous_obj['paid_amounts']
          subscription_object['access_rights_enabled'] = previous_obj['access_rights_enabled']
          subscription_object['hidden'] = previous_obj['hidden']
          subscription_object['all_payment_history_events'] = previous_obj['all_payment_history_events']
          subscription_object['all_subscription_modification_events'] = previous_obj['all_subscription_modification_events']
          subscription_object['income_stream_data_points'] = previous_obj['income_stream_data_points']
        }
      }

      if(subscription_data != null){
        const index = created_subscription_object_data.findIndex(item => item['e5_id'] === subscription_object['e5_id']);
        if(index != -1){
          created_subscription_object_data[index] = subscription_object
        }else{
          created_subscription_object_data.push(subscription_object)
        }
        created_subscription_object_mapping[created_subscriptions[i]] = subscription_object
      }
      

      if(is_first_time || true){
        var created_subscription_object_data_clone = structuredClone(this.state.created_subscriptions)
        created_subscription_object_data_clone[e5] = created_subscription_object_data
        
        var created_subscription_object_mapping_clone = structuredClone(this.state.created_subscription_object_mapping)
        created_subscription_object_mapping_clone[e5] = created_subscription_object_mapping

        this.setState({created_subscriptions: created_subscription_object_data_clone, created_subscription_object_mapping: created_subscription_object_mapping_clone})
        // await this.wait(150)
        console.log('get_subscription_data', 'done setting created_subscriptions into state', created_subscription_object_data_clone)

        var me = this;
        setTimeout(function() {
          if((i%me.state.update_search_object_load_count == 0 || i == created_subscriptions.length-1)){
            me.homepage.current?.start_update_search(me.getLocale()['1200']/* 'subscriptions' */)
          }
        }, (1 * 500));
      }

      if(i+1 >= load_pos && i < all_return_data_loaded_event_count){
        const end = load_pos+this.state.sliced_object_load_increment_count > all_return_data_loaded_event_count ? all_return_data_loaded_event_count : load_pos+this.state.sliced_object_load_increment_count
        all_data = await this.fetch_multiple_objects_data(created_subscriptions.slice(i+1, end), web3, e5, contract_addresses, all_return_data, 'objects_data')
        load_pos += this.state.sliced_object_load_increment_count

        const alias_accounts = this.get_event_account_ids(created_subscription_events.slice(i+1, end), 'p3')
        await this.get_alias_data_for_accounts(E52contractInstance, e5, alias_accounts, web3)
      }else
      if(i+1 >= load_pos){
        await this.wait(1000)
        all_data = await this.fetch_multiple_objects_data(created_subscriptions.slice(i+1, i+this.state.event_load_chunk_size), web3, e5, contract_addresses)

        const alias_accounts = this.get_event_account_ids(created_subscription_events.slice(i+1, i+this.state.event_load_chunk_size), 'p3')
        await this.get_alias_data_for_accounts(E52contractInstance, e5, alias_accounts, web3)

        load_pos += this.state.event_load_chunk_size
      }
    }

    var created_subscription_object_data_clone = structuredClone(this.state.created_subscriptions)
    created_subscription_object_data_clone[e5] = created_subscription_object_data
    
    var created_subscription_object_mapping_clone = structuredClone(this.state.created_subscription_object_mapping)
    created_subscription_object_mapping_clone[e5] = created_subscription_object_mapping

    this.setState({created_subscriptions: created_subscription_object_data_clone, created_subscription_object_mapping: created_subscription_object_mapping_clone})
    
    // console.log('subscription count for e5: ',e5,' : ',created_subscription_object_data.length)
  }

