  get_poll_data = async (E52contractInstance, web3, e5, contract_addresses, prioritized_accounts, specific_items, account, return_created_object_events_only=false, all_return_data={}) => {
    var created_post_events = (this.state.saved_pre_launch_events[e5] != null && this.state.created_polls[e5] == null) || return_created_object_events_only == true ? this.state.saved_pre_launch_events[e5]['created_poll_events'] :await this.load_event_data(web3, E52contractInstance, 'e2', e5, {p3/* item_type */: 28/* 28(poll-object) */, p1:this.get_valid_post_index(web3)})
    created_post_events = created_post_events.slice().reverse()


    //prioritize the polls ive participated in first
    if(this.state.my_polls.length > 0){
      //prioritize my participated in polls first
      var my_participated_poll_ids = this.filter_accounts_by_e5(this.state.my_polls, e5)
      var my_participated_events = created_post_events.filter(function (event) {
        return (my_participated_poll_ids.includes(event.returnValues.p2))
      })
      created_post_events.forEach(event => {
        if(my_participated_events.find(e => e.returnValues.p2 === event.returnValues.p2) == null){
          my_participated_events.push(event)
        }
      });
      created_post_events = my_participated_events
    }

    if(prioritized_accounts && prioritized_accounts.length > 0){
      var my_posted_events = created_post_events.filter(function (event) {
        return (prioritized_accounts.includes(parseInt(event.returnValues.p5/* sender_account_id */)) || prioritized_accounts.includes(parseInt(event.returnValues.p2/* object_id */)))
      })
      created_post_events = my_posted_events
    }
    else{
      //prioritize my content first
      var my_posted_events = created_post_events.filter(function (event) {
        return (event.returnValues.p5/* sender_account */ == account)
      })
      created_post_events.forEach(event => {
        if(my_posted_events.find(e => e.returnValues.p2 === event.returnValues.p2) == null){
          my_posted_events.push(event)
        }
      });
      created_post_events = my_posted_events
    }

    if(specific_items != null && specific_items.length > 0){
      var my_events = created_post_events.filter(function (event) {
        return (specific_items.includes(event.returnValues.p2))
      })
      created_post_events.forEach(event => {
        if(my_events.find(e => e.returnValues.p2 === event.returnValues.p2) == null){
          my_events.push(event)
        }
      });
      created_post_events = my_events
    }

    if(return_created_object_events_only == true){
      return created_post_events
    }
    
    this.record_number_of_items(e5, 'poll', created_post_events.length)
    var created_posts = this.state.created_polls[e5] == null ? [] : this.state.created_polls[e5].slice()
    var is_first_time = this.state.created_polls[e5] == null

    const all_return_data_loaded_event_count = this.get_ids_from_events(created_post_events).slice(0, this.state.max_post_bulk_load_count).length
    var all_data = await this.fetch_multiple_objects_data(this.get_ids_from_events(created_post_events).slice(0, this.state.sliced_object_load_count), web3, e5, contract_addresses, all_return_data, 'objects_data')
    var load_pos = this.state.sliced_object_load_count

    for(var i=0; i<created_post_events.length; i++){
      var id = created_post_events[i].returnValues.p2
      var hash = web3.utils.keccak256('en')
      if(created_post_events[i].returnValues.p1.toString() == hash.toString()|| this.is_post_index_valid(created_post_events[i].returnValues.p1.toString(), web3)){
        var post_data = all_data[id] == null ? await this.fetch_objects_data(id, web3, e5, contract_addresses): all_data[id]

        const data = {'id':id, 'ipfs':post_data, 'event': created_post_events[i], 'e5':e5, 'timestamp':parseInt(created_post_events[i].returnValues.p6), 'author':created_post_events[i].returnValues.p5, 'e5_id':id+e5, 'object_type':'poll'}

        const index = created_posts.findIndex(item => item['e5_id'] === data['e5_id']);
        if(index != -1){
          created_posts[index] = data
        }else{
          created_posts.push(data)
        }
      }

      if(is_first_time || true){
        var created_polls_clone = structuredClone(this.state.created_polls)
        created_polls_clone[e5] = created_posts
        this.setState({created_polls: created_polls_clone}) 

        var me = this;
        setTimeout(function() {
          if((i%me.state.update_search_object_load_count == 0 || i == created_post_events.length-1)){
            me.homepage.current?.start_update_search(me.getLocale()['1264ao']/* 'polls' */)
          }
        }, (1 * 500));
      }

      if(i+1 >= load_pos && i < all_return_data_loaded_event_count){
        const end = load_pos+this.state.sliced_object_load_increment_count > all_return_data_loaded_event_count ? all_return_data_loaded_event_count : load_pos+this.state.sliced_object_load_increment_count
        all_data = await this.fetch_multiple_objects_data(this.get_ids_from_events(created_post_events).slice(i+1, end), web3, e5, contract_addresses, all_return_data, 'objects_data')

        const alias_accounts = this.get_event_account_ids(created_post_events.slice(i+1, end), 'p5')
        await this.get_alias_data_for_accounts(E52contractInstance, e5, alias_accounts, web3)

        load_pos += this.state.sliced_object_load_increment_count
      }else
      if(i+1 >= load_pos){
        await this.wait(1000)
        all_data = await this.fetch_multiple_objects_data(this.get_ids_from_events(created_post_events).slice(i+1, i+this.state.event_load_chunk_size), web3, e5, contract_addresses)

        const alias_accounts = this.get_event_account_ids(created_post_events.slice(i+1, i+this.state.event_load_chunk_size), 'p5')
        await this.get_alias_data_for_accounts(E52contractInstance, e5, alias_accounts, web3)

        load_pos += this.state.event_load_chunk_size
      }
    }

    var created_posts_clone = structuredClone(this.state.created_polls)
    created_posts_clone[e5] = created_posts
    this.setState({created_polls: created_posts_clone})

  }

