  fetch_multiple_objects_data = async (ids_to_load, web3, e5, addresses, pre_launch_data={}, target='') => {
    var load_limit = 1023
    if(ids_to_load.length == 0){
      return {}
    }
    if(this.state.beacon_chain_url != ''){
      load_limit = this.state.beacon_data['hash_data_request_limit']
    }
    if(this.state.my_preferred_nitro != '' && this.get_nitro_link_from_e5_id(this.state.my_preferred_nitro) != null && this.state.nitro_node_details[this.state.my_preferred_nitro] != null){
      load_limit = this.state.nitro_node_details[this.state.my_preferred_nitro]['hash_data_request_limit']
    }
    else if(this.state.beacon_node_enabled == false){
      console.log('all_data', 'beacon node offline')
      return {}
    }
    const ids = ids_to_load.slice(0, load_limit)

    if(pre_launch_data[e5] != null && target != ''){
      const id_to_hash_mapping = pre_launch_data[e5][target]['id_to_hash_mapping']
      const hash_ids = Object.keys(id_to_hash_mapping)
      var obj_id_ecid = {}
      var hashes = []
      var valid_ids = []
      var obj_types = {}
      
      // if(target == 'job_objects_data') console.log('apppage', 'fetch_multiple_objects_data', 'ids', ids);
      // if(target == 'job_objects_data') console.log('apppage', 'fetch_multiple_objects_data', 'hash_ids', hash_ids);
      hash_ids.forEach(loaded_id => {
        const ecid = id_to_hash_mapping[loaded_id]
        if(ecid != 'e3' && ecid != 'e2' && ecid != 'e1' && ecid != 'e'){
          try{
            var cid = ecid
            var option = 'in'
            if(ecid.includes('.')){
              var split_cid_array = ecid.split('.');
              option = split_cid_array[0]
              cid = split_cid_array[1]
            }
            var id = cid;
            var internal_id = ''
            if(cid.includes('_')){
              var split_cid_array2 = cid.split('_');
              id = split_cid_array2[0]
              internal_id = split_cid_array2[1]
            }
            if(id.includes('-')){
              var split_cid_array3 = id.split('-');
              id = split_cid_array3[1]
            }
            if(ids.includes(loaded_id)){
              obj_id_ecid[loaded_id] = {'id':id, 'internal_id':internal_id,'option':option }
              obj_types[id] = option
              hashes.push(id)
              valid_ids.push(loaded_id)
            }
          }catch(e){
            console.log('apppage', 'fetch_multiple_objects_data', e)
          }
        }
      });

      // if(target == 'job_objects_data'){
      // console.log('apppage', 'fetch_multiple_objects_data', 'obj_id_ecid', obj_id_ecid)
      // console.log('apppage', 'fetch_multiple_objects_data', 'obj_types', obj_types)
      // console.log('apppage', 'fetch_multiple_objects_data', 'get_post_data', 'hashes', hashes)
      // console.log('apppage', 'fetch_multiple_objects_data', 'get_post_data', 'valid_ids', valid_ids)
      // }

      const all_events_data = pre_launch_data[e5][target]['object_hash_data']
      if(Object.keys(all_events_data).length > 0){
        for(var i=0; i<hashes.length; i++){
          var cid_data = all_events_data[hashes[i]]
          if(cid_data != null){
            try{
              var decrypted_data = obj_types[hashes[i]] == 'ni' ? await this.decrypt_storage_object(cid_data) : await this.decrypt_storage_object2(cid_data);
              this.store_in_local_storage(hashes[i], JSON.parse(decrypted_data))
              await this.wait(50)
            }catch(e){
              console.log('fetch_multiple_objects_data', e)
            }
          }
        }
      }
      // if(target == 'job_objects_data')console.log('apppage', 'fetch_multiple_objects_data', 'stored ids in local storage...');
      await this.wait(300)

      var return_data = {}
      for(var l=0; l<valid_ids.length; l++){
        var valid_id = valid_ids[l]
        var valid_id_cid = obj_id_ecid[valid_id]['id']
        var valid_id_internal_id = obj_id_ecid[valid_id]['internal_id']
        var valid_id_data = await this.fetch_from_storage(valid_id_cid)
        if(valid_id_data != null){
          var final_data = valid_id_data
          // console.log('apppage', 'fetch_multiple_objects_data', 'get_post_data',  'data object for', valid_id, final_data)
          if(valid_id_internal_id != ''){
            final_data = valid_id_data[valid_id_internal_id]
          }
          return_data[valid_id] = final_data
        }else{
          // console.log('apppage', 'fetch_multiple_objects_data', 'get_post_data',  'failed to load', valid_id)
        }
      }

      // if(target == 'job_objects_data') console.log('apppage', 'fetch_multiple_objects_data', 'return_data', return_data);
      return return_data
    }

    var event_params = []
    for(var i=0; i<ids.length; i++){
      const E52contractArtifact = require('./contract_abis/E52.json');
      const E52_address = addresses[1];
      const E52contractInstance = new web3.eth.Contract(E52contractArtifact.abi, E52_address);
      var target_id = ids[i];
      event_params.push([web3, E52contractInstance, 'e5', e5, {p1/* target_obj_id */: target_id}])
    }
    var { all_events, all_events_data } = await this.load_multiple_events_from_nitro(event_params, 'p4')
    var obj_id_ecid = {}
    var hashes = []
    var valid_ids = []
    var obj_types = {}
    console.log('apppage', 'all events length', all_events, all_events_data)

    for(var i=0; i<all_events.length; i++){
      var objects_event = all_events[i]
      if(objects_event.length != 0){
        var ecid = objects_event[objects_event.length - 1].returnValues.p4
        if(ecid != 'e3' && ecid != 'e2' && ecid != 'e1' && ecid != 'e'){
          try{
            var cid = ecid
            var option = 'in'
            if(ecid.includes('.')){
              var split_cid_array = ecid.split('.');
              option = split_cid_array[0]
              cid = split_cid_array[1]
            }
            var id = cid;
            var internal_id = ''
            if(cid.includes('_')){
              var split_cid_array2 = cid.split('_');
              id = split_cid_array2[0]
              internal_id = split_cid_array2[1]
            }
            obj_id_ecid[ids[i]] = {'id':id, 'internal_id':internal_id,'option':option }
            obj_types[id] = option
            if(await this.fetch_from_storage(id) == null) hashes.push(id)
            valid_ids.push(ids[i])
          }catch(e){
            console.log('apppage', e)
          }
        }
      }
    }

    var has_hash_data_already_been_loaded = false;
    if(Object.keys(all_events_data).length > 0){
      for(var i=0; i<hashes.length; i++){
        var cid_data = all_events_data[hashes[i]]
        if(cid_data != null){
          var confirmation_hash = await this.generate_hash(JSON.stringify(cid_data))
          if(confirmation_hash != hashes[i] && obj_types[hashes[i]] == 'ni'){
            console.log('apppage', hashes[i], 'data has been modified! bad data!', confirmation_hash)
          }else{
            try{
              var decrypted_data = obj_types[hashes[i]] == 'ni' ? await this.decrypt_storage_object(cid_data) : await this.decrypt_storage_object2(cid_data);
              // console.log('apppage', 'decrypted object', decrypted_data)
              this.store_in_local_storage(hashes[i], JSON.parse(decrypted_data))
            }catch(e){
              console.log(e)
            }
          }
        }
      }
      has_hash_data_already_been_loaded = true;
    }

    if(has_hash_data_already_been_loaded == false){
      // console.log('apppage', 'obj_id_ecid', obj_id_ecid, 'hashes', hashes)
      var beacon_node = `${process.env.REACT_APP_BEACON_NITRO_NODE_BASE_URL}`
      if(this.state.beacon_chain_url != '') beacon_node = this.state.beacon_chain_url;
      if(this.state.my_preferred_nitro != '' && this.get_nitro_link_from_e5_id(this.state.my_preferred_nitro) != null){
        beacon_node = this.get_nitro_link_from_e5_id(this.state.my_preferred_nitro)
      }
      const arg_string_data = await this.encrypt_arg_string(beacon_node, JSON.stringify({hashes: hashes}))
      const params = new URLSearchParams({
        arg_string: arg_string_data,
      });
      var request = `${beacon_node}/${this.load_registered_endpoint_from_link(beacon_node, 'data')}/${await this.fetch_nitro_privacy_signature(beacon_node)}?${params.toString()}`
      try{
        const response = await fetch(request);
        if (!response.ok) {
          console.log('datas',response)
          throw new Error(`Failed to retrieve data. Status: ${response}`);
        }
        var data = await response.text();
        var obj = await this.process_nitro_api_call_result(data, beacon_node);
        var object_data = obj['data']
        console.log('apppage', 'fetch_multiple_objects_data', obj)
        for(var i=0; i<hashes.length; i++){
          var cid_data = object_data[hashes[i]]
          if(cid_data != null){
            var confirmation_hash = await this.generate_hash(JSON.stringify(cid_data))
            if(confirmation_hash != hashes[i] && obj_types[hashes[i]] == 'ni'){
              console.log('apppage', hashes[i], 'data has been modified! bad data!', confirmation_hash)
            }else{
              try{
                var decrypted_data = obj_types[hashes[i]] == 'ni' ? await this.decrypt_storage_object(cid_data) : await this.decrypt_storage_object2(cid_data);
                // console.log('apppage', 'decrypted object', decrypted_data)
                this.store_in_local_storage(hashes[i], JSON.parse(decrypted_data))
              }catch(e){
                console.log(e)
              }
            }
          }
        }
      }
      catch(e){
        console.log('apppage', e)
        return {}
      }
    }

    var data = {}
    for(var i=0; i<valid_ids.length; i++){
      var valid_id = valid_ids[i]
      var valid_id_cid = obj_id_ecid[valid_id]['id']
      var valid_id_internal_id = obj_id_ecid[valid_id]['internal_id']
      var valid_id_data = await this.fetch_from_storage(valid_id_cid)
      if(valid_id_data != null){
        var final_data = valid_id_data
        if(valid_id_internal_id != ''){
          final_data = valid_id_data[valid_id_internal_id]
        }
        data[valid_id] = final_data
      }
    }

    console.log('apppage', 'return', data)
    return data
  }

  fetch_objects_data = async (id, web3, e5, addresses) => {
    const E52contractArtifact = require('./contract_abis/E52.json');
    const E52_address = addresses[1];
    const E52contractInstance = new web3.eth.Contract(E52contractArtifact.abi, E52_address);
    var target_id = id;
    var events = await this.load_event_data(web3, E52contractInstance, 'e5', e5, {p1/* target_obj_id */: target_id})
    if(events.length == 0) return;
    var n = 1
    var cid = events[events.length - n].returnValues.p4
    if(cid == 'e3' || cid == 'e2' || cid == 'e1' || cid == 'e') return;

    var data = null
    while(data == null && events.length >= n){
      var cid = events[events.length - n].returnValues.p4
      data = await this.fetch_objects_data_from_ipfs_using_option(cid)
      if(data == null){
        n++
      }
    }
    return data
  }



