    if(ecid == 'ar.MY_Kh9i3VhChJuTzo_Cyu6bG6hCvasqzWGfo4oniNnA_1xjiAYxW') return null
    if(!ecid.includes('.')){
      var data = await this.fetch_object_data_from_infura(ecid)
      return data
    }
    var split_cid_array = ecid.split('.');
