  get_object_size_in_mbs(obj) {
    const bytes = new TextEncoder().encode(JSON.stringify(obj, (_, v) => typeof v === 'bigint' ? v.toString() : v)).length;
    return (bytes / (1024 * 1024)).toFixed(2); // Convert bytes to MB
  }

