  async get_objects_messages_from_socket_and_enter_chatroom(target){
    if(!target.startsWith('job_request_mail') && !target.startsWith('mail')) this.enter_chatroom_if_socket_enabled(target);
    const absolute_load_limit = /* Date.now() - (7200*7*24*60*60*1000) */ 0
    const load_step = /* target.startsWith('job_request_mail') ? (7100*7*24*60*60*1000) : (3000*7*24*60*60*1000) */ Date.now() - 1000;
    var current_filter_end_time = Date.now() - load_step
    var current_filter_start_time = Date.now()
    
    while(absolute_load_limit < current_filter_end_time){
      //target, filter_end_time=(Date.now() - (52*7*24*60*60*1000)), filter_start_time=(Date.now()), size_limit_in_kbs=(1024*10)
      const socket_data = await this.get_socket_data([target], current_filter_end_time, current_filter_start_time, (1024*100), [])
      if(socket_data == null){
        current_filter_end_time -= load_step
        current_filter_start_time -= load_step
        continue;
      } 
      const target_data = socket_data
      if(target_data != null){
        const entries = Object.keys(target_data)
        for(var j=0; j<entries.length; j++){
          const time_entry = entries[j]
          const target_entries = Object.keys(target_data[time_entry])
          for(var k=0; k<target_entries.length; k++){
            const target_entry = target_entries[k]
            const object_hashes = Object.keys(target_data[time_entry][target_entry])
            for(var i=0; i<object_hashes.length; i++){
              const object_hash = object_hashes[i]
              const object_data = target_data[time_entry][target_entry][object_hash]
              if(object_data['type'] == 'mail-message'){
                await this.process_new_message_received(object_data, object_hash, null, false)
              }
              else if(object_data['type'] == 'job-request-message'){
                await this.process_new_job_request_message(object_data, object_hash, null)
              }
              else if(object_data['type'] == 'channel-message'){
                await this.process_new_channel_message(object_data, object_hash)
              }
              else{
                await this.process_new_comment_message(object_data, object_hash)
              }
            }
          }
        }
        
        current_filter_end_time -= load_step
        current_filter_start_time -= load_step
      }
      else{
        current_filter_end_time -= load_step
        current_filter_start_time -= load_step
      }
      await this.wait(2000)
    }
  }

  get_object_comment_events = async (id, e5, target_id) => {
    var all_unsorted_events = []
    const cutoff_timestamp = /* Math.round(Date.now()/1000) - (60*60*24*400000) */ 0
    if((this.state.my_preferred_nitro != '' && this.get_nitro_link_from_e5_id(this.state.my_preferred_nitro) != null) || this.state.beacon_node_enabled == true){
      const event_params = []
      const used_e5s = []
      for(var i=0; i<this.state.e5s['data'].length; i++){
        const focused_e5 = this.state.e5s['data'][i]
        if(this.state.addresses[focused_e5] != null){
          used_e5s.push(focused_e5)
          const web3 = new Web3(this.get_web3_url_from_e5(focused_e5));
          const E52contractArtifact = require('./contract_abis/E52.json');
          const E52_address = this.state.addresses[focused_e5][1];
          const E52contractInstance = new web3.eth.Contract(E52contractArtifact.abi, E52_address);
          var e5_id = parseInt(e5.replace('E',''))
          event_params.push([web3, E52contractInstance, 'e4', focused_e5, {p1/* target_id */: target_id, p3/* context */:id, p5: e5_id,}])
        }
      }
      const { all_events, all_events_data } = await this.load_multiple_events_from_nitro(event_params, 'comment')
      await this.set_all_hashes_in_storage(all_events_data)
      console.log('apppage', 'get_object_messages', all_events)
      all_events.forEach((event_array, index)=> {
        // var focused_e5 = used_e5s[index]
        var m = 0
        for(var l=0; l<event_array.length; l++){
          const event = event_array[l]
          const focused_e5 = 'E'+event.returnValues.p5
          event['e5'] = focused_e5
          if(parseInt(event.returnValues.p6) > cutoff_timestamp){
            var time = parseInt(event.returnValues.p6)/* timestamp */
            if(m!=0){
              const prev_event_object = all_unsorted_events[m-1]
              if(prev_event_object['time'] == time){
                time++
              }
            }
            all_unsorted_events.push({'time':time, 'event':event})
            m++
          }
        }
      });
    }else{
      for(var i=0; i<this.state.e5s['data'].length; i++){
        const focused_e5 = this.state.e5s['data'][i]
        if(this.state.addresses[focused_e5] != null){
          const web3 = new Web3(this.get_web3_url_from_e5(focused_e5));
          const E52contractArtifact = require('./contract_abis/E52.json');
          const E52_address = this.state.addresses[focused_e5][1];
          const E52contractInstance = new web3.eth.Contract(E52contractArtifact.abi, E52_address);
          const e5_id = parseInt(e5.replace('E',''))
          
          const created_comment_data = (await this.load_event_data(web3, E52contractInstance, 'e4', focused_e5, {p1/* target_id */: target_id, p3/* context */:id, p5: e5_id,}))
          console.log('apppage', 'loaded_events for e5',focused_e5, created_comment_data)
          var m = 0
          for(var k=0; k<created_comment_data.length; k++){
            const event = created_comment_data[k]
            event['e5'] = 'E'+event.returnValues.p5
            if(parseInt(event.returnValues.p6) > cutoff_timestamp){
              var time = parseInt(event.returnValues.p6)/* timestamp */
              if(m!=0){
                var prev_event_object = all_unsorted_events[m-1]
                if(prev_event_object['time'] == time){
                  time++
                }
              }
              all_unsorted_events.push({'time':event.returnValues.p6/* timestamp */, 'event':event})
              m++
            }
          }
        }
      }
    }

    var sorted_object_events = this.sortByAttributeDescending(all_unsorted_events, 'time')
    var events = []
    sorted_object_events.forEach(object => {
      const includes = events.find(e => e.returnValues.p4 === object['event'].returnValues.p4)
      if(includes == null){
        events.push(object['event'])
      }
    });

    return events
  }

