  structuredClone(page_data){
    var clone = {}
    for (var key in page_data) {
      if (page_data.hasOwnProperty(key)) {
        clone[key] = page_data[key];
      }
    }
    return clone

    // return JSON.parse(JSON.stringify(page_data))
  }

